import React from 'react'
import axios from 'axios';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { PureComponent } from 'react';


class ActivityGraph extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {value: '', eventData: []};
    this.getEventRates = this.getEventRates.bind(this);
    this.data = [ { monitor: "2023-05-19T05:39:31", uv: 203362, pv: 2400, amt: 2400 }, { monitor: "2023-05-19T05:40:31", uv: 371888, pv: 1398, amt: 2210 }, { monitor: "2023-05-19T05:50:31", uv: 122000, pv: 3333.23, amt: 2290 }, { monitor: "2023-05-19T06:40:31", uv: 144888, pv: 3908.44, amt: 2000 }, { monitor: "2023-05-19T07:40:31", uv: 1890, pv: 4800.11, amt: 2181 }, { monitor: "2023-05-19T08:40:31", uv: 55533, pv: 1144.4, amt: 2500 }, { monitor: "2023-05-19T09:40:31", uv: 112000, pv: 4300.11, amt: 2100 } ];
  }

  getEventRates() {
    axios({
        method: "get",
        url: "/api/datarate/",
        headers: { "accept": "application/json", "Access-Control-Allow-Origin": "*"}
      }).then((response) => {
        this.state.eventData = response.data

      }).catch(err => console.log(err))
  }

  componentDidMount() {
    this.getEventRates()
  }

  render() {
    return (
      <div style={{ width: '85%' }}>
        <h4>&nbsp;&nbsp;&nbsp;Event Rate (per sec)</h4>

        <ResponsiveContainer height={200}>
          <AreaChart
            width={500}
            height={200}
            data={this.state.eventData}
            syncId="anyId"
            margin={{
              top: 10,
              right: 30,
              left: 0,
              bottom: 0,
            }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="monitor" />
            <YAxis />
            <Tooltip />
            <Area type="monotone" dataKey="count" stroke="#82ca9d" fill="#005073" />
          </AreaChart>
        </ResponsiveContainer>
      </div>
    );
  }
}

export default ActivityGraph;
