import React from 'react';
import FormHeader from './FormHeader';
import axios from 'axios';
import Header from './Header.js';
import SideBar from './SideBar.js';
import Outputters from './Outputters.js';
import './cisco-atomic-ui.scss';
import Table from './Table.js';
import { Output } from './Output.js';
import {useState, useEffect} from 'react'

class Config extends React.Component {

  constructor(props) {
    super(props);
    this.state = {value: '', lastWritten: []};
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.getLastWritten = this.getLastWritten.bind(this);
    this.getStatus = this.getStatus.bind(this);

    this.awsRegion = React.createRef();
    this.certPass = React.createRef();
  }

  getLastWritten() {
    axios({
        method: "get",
        url: "/api/lastwritten/",
        headers: { "accept": "application/json", "Access-Control-Allow-Origin": "*"}
      }).then((response) => {
        this.state.lastWritten = response.data
        console.log(response.data)

      }).catch(err => console.log(err))
  }

  getStatus() {
    axios({
        method: "get",
        url: "/api/status/",
        headers: { "accept": "application/json", "Access-Control-Allow-Origin": "*"}
      }).then((response) => {
        this.state.status = response.data.state.description
        console.log(response.data)

      }).catch(err => console.log(err))
  }

  afterSubmission(event) {
      event.preventDefault();
  }

  componentDidMount() {
    this.getLastWritten()
    this.getStatus()
  }

  handleChange(event) {
    event.preventDefault();
    this.setState({file: event.target.files[0]});
    const formData = new FormData();
    formData.append("file", this.state.file);

    try {
      axios({
        method: "post",
        url: "/api/uploadfile/",
        data: formData,
        headers: { "Content-Type": "multipart/form-data", "Access-Control-Allow-Origin": "*", "content": "application/json"},
      });
    } catch(error) {
      console.log(error)
    }
    alert('A name was submitted: ' + this.state.file);
  }

  handleSubmit(event) {
    event.preventDefault();
    const formData = new FormData();
    const awsRegion = this.awsRegion.current.value

    formData.append("aws_region", awsRegion);
    formData.append("aws_source", "source");

    var dict_t = [];
    dict_t = {"aws_region":awsRegion,"aws_source":"source"};

    try {
      axios({
        method: "post",
        url: "/api/modifyconfig/",
        data: dict_t,
        headers: { "Content-Type": "application/json"},
      });
    } catch(error) {
      console.log(error)
    }
  }


  handleStart(event) {

    try {
      axios({
        method: "post",
        url: "/api/execute/",
        headers: { "Content-Type": "multipart/form-data", "Access-Control-Allow-Origin": "*", "content": "application/json"},
      });
    } catch(error) {
      console.log(error)
    }
    alert('Running eNcore ');
    event.preventDefault();
  }

  handleStop(event) {

    try {
      axios({
        method: "post",
        url: "/api/executestop/",
        headers: { "Content-Type": "multipart/form-data", "Access-Control-Allow-Origin": "*", "content": "application/json"},
      });
    } catch(error) {
      console.log(error)
    }
    alert('Stopping eNcore ');
    event.preventDefault();
  }

  fileUploadForm(props) {
     const fileExists = 0;

     if (fileExists) {
       return <input id="certFile" type="file" onChange={this.handleChange} />;
     }
     else {
       return <input id="certFile" type="file" placeholder="client.pkcs12" onChange={this.handleChange} />;
     }
  }


  render() {

    return (
      <body class="atomic-ui-root">
        <div class="banner">
          <Header/>
          <div class="container-fluid">
            <div class="row semi-flexible">
              <div><SideBar/></div>
                <div>
                  <div class="col-md-12 content-container">
                    <h3>Setup Process</h3>
                    <div><FormHeader/></div>
                    <div class="panel">
                      <h3>Configuration</h3>
		            <div class="form-group">
		              <form onSubmit={this.handleSubmit}>
		                <div class="form-group__text">
		                  <label class="label">
		                    FMC URL: &nbsp; &nbsp;
		                  <input id="fmcUrl" type="text" class="input" name="fmcUrl" placeholder={this.props.data.host} />
		                  </label>
		                </div>
		                <div class="form-group__text">
		                  <label class="label">
		                    FMC Certificate: &nbsp; &nbsp;    
                                    {this.fileUploadForm(this.props)}
		                    &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 
		                    Password &nbsp; &nbsp;
		                    <input id="certPass" type="password" />
		                  </label>
		                </div>
		                <div class="form-group__text">
		                  <label class="label">
		                    AWS Account: &nbsp; &nbsp;
		                    <input id="awsAccount" type="text" class="input" name="aws_account" placeholder={this.props.data.accountId}/>
		                  </label>
		                </div>
		                <div class="form-group__text">
		                  <label class="label">
		                    AWS Region: &nbsp; &nbsp;
		                  <input id="awsRegion" ref={this.awsRegion} type="text" class="input" name="aws_region" placeholder={this.props.data.region}/>
		                  </label>
		                </div>
                                <div class="form-group__text">
                                  <label class="label">
                                    AWS Data Lake Source: &nbsp; &nbsp;
                                  <input id="awsSource" type="text" class="input" name="aws_source" placeholder={this.props.data.awssource}/>
                                  </label>
                                </div>
                                <label class="label">Buffer to S3 Window</label>
                                <div class ="select">
                                  <select class="select__native-select" width="40">
                                    <option selected="selected" value="5">5 Min</option>
                                    <option value="10">10 Min</option>
                                    <option value="15">15 Min </option>
                                  </select>
                                </div>
		                <input type="submit" class="btn btn--primary" value="Submit" />
		              </form>
		            </div>
                    </div>
                  </div>
                </div>
              </div>
	    </div>
          </div>
      </body>
    );

  }
}

export default Config;
