import React from 'react';
import axios from 'axios';
import Outputters from './Outputters.js';
import './cisco-atomic-ui.scss';
import Table from './Table.js';
import { Output } from './Output.js';
import {useState, useEffect} from 'react'
import SideBar from './SideBar.js';
import Header from './Header.js';
import ActivityGraph from './ActivityGraph.js';

class Monitor extends React.Component {

  constructor(props) {
    super(props);
    this.state = {value: '', awsResponse: []};
    this.getAWSResponse = this.getAWSResponse.bind(this);
  }

  handleChange(event) {    this.setState({file: event.target.files[0]});  }

  getAWSResponse() {
    axios({
        method: "get",
        url: "/api/awsresponse/",
        headers: { "accept": "application/json", "Access-Control-Allow-Origin": "*"}
      }).then((response) => {
        this.state.awsResponse = response.data
        console.log(response.data)

      }).catch(err => console.log(err))
  }

  componentDidMount() {
    this.getAWSResponse()
  }

  render() {
    const s3URI = "s3://aws-security-data-lake/ext/CISCOFIREWALL/region=us-east-2/accountId=551076683564/eventDay=20230308/"
    return (
      <body class="atomic-ui-root">
        <div class="banner">
          <Header/>
          <div class="container-fluid">
            <div class="row semi-flexible">
            <div><SideBar/></div>
            <div>
            <div><ActivityGraph/></div>
	    <div class="col-md-12 content-container">
	      <div class="content">
	        <div class="row">
                  <div class="col-md-12">
                    <div class="panel">
                      <div class="table-admins">
                        <h3>Files Sent to AWS Security Lake</h3>
                        <table class="table">
                          <thead>
                            <tr class="table__header-row">
                              <th class="table__header-cell">Request ID</th>
                              <th class="table__header-cell">Host ID</th>
                              <th class="table__header-cell">HTTP Status</th>
                              <th class="table__header-cell">HTTP Headers</th>
                              <th class="table__header-cell">Filename</th>
                              <th class="table__header-cell">Creation Date</th>
                              <th class="table__header-cell">Transmitted</th>
                              <th class="table__header-cell">eTag</th>
                              <th class="table__header-cell">S3 URI</th>
                            </tr>
                           </thead>
                           <tbody>
                             <tr class="table__row">
                               <td class="table__cell">1678286335.gz.parquet</td>
                               <td class="table__cell">{this.state.awsResponse.HostId}</td>
                               <td class="table__cell">April 13, 2023, 18:31:55 (UTC-04:00)</td>
                               <td class="table__cell">4.6 MB</td>
                               <td class="table__cell">Success</td>
                               <td class="table__cell"><a href ={s3URI}>link</a></td>
                             </tr>
                           </tbody>
                          </table>
                        </div>
                      </div>
                    </div>
                </div>
              </div>
              </div>
              </div>
            </div>
          </div>
        </div>
      </body>
    );
  }
}

export default Monitor;
